-- Make-Tables

UNLOCK TABLES;
-- e.g. a specific company
CREATE TABLE customer(
    id INT(4) NOT NULL,  
    name VARCHAR(20) NOT NULL, -- e.g. brightsolid
    PRIMARY KEY (id)
); 
LOCK TABLE customer WRITE;

UNLOCK TABLES;
-- manager or auditor
CREATE TABLE user_role(
    id INT(4) NOT NULL,
    role VARCHAR(20) NOT NULL, 
    PRIMARY KEY (id)
);
LOCK TABLE user_role WRITE;

UNLOCK TABLES;
-- e.g. user of the company (customer)
CREATE TABLE user(
    id INT(4) NOT NULL,
    name VARCHAR(50),
    role_id INT(4) NOT NULL,
    customer_id INT(4) NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (role_id) REFERENCES user_role(id),
    FOREIGN KEY (customer_id) REFERENCES customer(id)
);
LOCK TABLE user WRITE;


UNLOCK TABLES;
CREATE TABLE login(
    id INT(4) NOT NULL,
    username VARCHAR(20),
    password VARCHAR(50), -- need to be hashed
    user_id INT(4) NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (user_id) REFERENCES user(id)
);
LOCK TABLE login WRITE;

UNLOCK TABLES;
CREATE TABLE platform(
    id INT(4) NOT NULL,
    name VARCHAR(10) NOT NULL,
    PRIMARY KEY (id)
);
LOCK TABLE platform WRITE;

UNLOCK TABLES;
CREATE TABLE account (
    id INT(4) NOT NULL,
    account_ref VARCHAR(50) NOT NULL,
    platorm_id INT(4) NOT NULL,
    customer_id INT(4) NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (platorm_id) REFERENCES platform(id),
    FOREIGN KEY (customer_id) REFERENCES customer(id)
);
LOCK TABLE account WRITE;

UNLOCK TABLES;
CREATE TABLE resource_type(
    id INT(4) NOT NULL,
    name VARCHAR(100) NOT NULL,
    platorm_id INT(4) NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (platorm_id) REFERENCES platform(id)
);
LOCK TABLE resource_type WRITE;

UNLOCK TABLES;
CREATE TABLE rule(
    id INT(4) NOT NULL,
    name VARCHAR(100) NOT NULL,
    resource_type_id INT(4) NOT NULL,
    description VARCHAR(100),
    PRIMARY KEY (id),
    FOREIGN KEY (resource_type_id) REFERENCES resource_type(id)
);
LOCK TABLE rule WRITE;


UNLOCK TABLES;
CREATE TABLE exception (
    id INT(4) NOT NULL,
    customer_id INT(4) NOT NULL,
    rule_id INT(4) NOT NULL,
    last_updated_by INT(4) NOT NULL,
    exception_value VARCHAR(50) NOT NULL,
    justification VARCHAR(80) NOT NULL,
    review_date TIMESTAMP NOT NULL,
    last_updated TIMESTAMP NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (customer_id) REFERENCES customer(id), 
    FOREIGN KEY (rule_id) REFERENCES rule(id), 
    FOREIGN KEY (last_updated_by) REFERENCES user(id)
);
LOCK TABLE exception WRITE;

UNLOCK TABLES;
CREATE TABLE resource(
    id VARCHAR(200) NOT NULL,
    account_id INT(4) NOT NULL,
    resource_type_id INT(4) NOT NULL,
    resource_name VARCHAR(100) NOT NULL,
    last_updated TIMESTAMP NOT NULL,
    resource_metadata VARCHAR(500) NOT NULL, 
    PRIMARY KEY (id),
    FOREIGN KEY (account_id) REFERENCES account(id),
    FOREIGN KEY (resource_type_id) REFERENCES resource_type(id)
);
LOCK TABLE resource WRITE;

UNLOCK TABLES;
CREATE TABLE non_compliance (
    id INT(4) NOT NULL,
    resource_id VARCHAR(200) NOT NULL,
    rule_id INT(4) NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (resource_id) REFERENCES resource(id),
    FOREIGN KEY (rule_id) REFERENCES rule(id)
);
LOCK TABLE non_compliance WRITE;


UNLOCK TABLES;
-- stores new added exceptions
CREATE TABLE non_compliance_audit (
    id INT(4) NOT NULL,
    non_compliance_id INT(4) NOT NULL,
    resource_id VARCHAR(200) NOT NULL, 
    rule_id INT(4) NOT NULL,
    user_id INT(4) NOT NULL,
    action VARCHAR(20) NOT NULL,
    action_dt TIMESTAMP NOT NULL,
    PRIMARY KEY (id),
    FOREIGN KEY (non_compliance_id) REFERENCES non_compliance(id),
    FOREIGN KEY (resource_id) REFERENCES resource(id),
    FOREIGN KEY (rule_id) REFERENCES rule(id),
    FOREIGN KEY (user_id) REFERENCES user(id)
);
LOCK TABLE non_compliance_audit WRITE;

UNLOCK TABLES;
-- stores history of exceptions of a specific resource
CREATE TABLE exception_audit(
    id INT(4) NOT NULL, 
    exception_id INT(4) NOT NULL,
    user_id INT(4) NOT NULL,
    customer_id INT(4) NOT NULL,
    rule_id INT(4) NOT NULL,
    action VARCHAR (20) NOT NULL, 
    action_dt TIMESTAMP  NOT NULL, 
    old_exception_value VARCHAR (200),
    new_exception_value VARCHAR (200),
    old_justification VARCHAR (200),
    new_justification VARCHAR (200),
    old_review_date TIMESTAMP,
    new_review_date TIMESTAMP,
    PRIMARY KEY (id),
    FOREIGN KEY (rule_id) REFERENCES rule(id), 
    FOREIGN KEY (exception_id) REFERENCES exception(id), 
    FOREIGN KEY (user_id) REFERENCES user(id),
    FOREIGN KEY (customer_id) REFERENCES customer(id)

);
LOCK TABLE exception_audit WRITE;



